mapper.select:
select id,devID,loVolt,devTime,devTypeArray,devNameArray,devValueArray
from sys_device_data a
WHERE a.devId = #{devId}

		SELECT id,devID,loVolt,devTime,devTypeArray,devNameArray,devValueArray
		FROM sys_device_data a
		WHERE 1 = 1

			AND a.devId = #{devId}


            AND a.devTime >=#{qsrq}


		    AND a.devTime <#{zzrq}

		ORDER BY a.devTime DESC

		SELECT id,devID,loVolt,devTime,devTypeArray,devNameArray,devValueArray
		FROM sys_device_data a
		WHERE 1 = 1

			AND a.devId = #{devId}


            AND a.devTime >=#{qsrq}


		    AND a.devTime <#{zzrq}

		ORDER BY a.devTime DESC

mapper.delete:
DELETE FROM sys_device_data 
WHERE 1 = 1

	AND id in #{item}
